/**
 * Results table component with responsive card layout for mobile
 * - Portions generated by AI
 */

import { useState } from 'react';
import { ChevronDown, ChevronUp } from 'lucide-react';
import type { OptionResult } from '@/types';

interface ResultsTableProps {
  data: OptionResult[];
  title: string;
}

function formatCurrency(value: number | undefined): string {
  if (value === undefined || value === null) return '';
  return `$${value.toFixed(2)}`;
}

function formatPercent(value: number | undefined): string {
  if (value === undefined || value === null) return '';
  return `${value.toFixed(1)}%`;
}

function getReturnClass(value: number): string {
  if (value >= 50) return 'text-green-400';
  if (value >= 30) return 'text-emerald-400';
  return 'text-blue-400';
}

function getReturnBgClass(value: number): string {
  if (value >= 50) return 'bg-green-500/20 border-green-500/30';
  if (value >= 30) return 'bg-emerald-500/20 border-emerald-500/30';
  return 'bg-blue-500/20 border-blue-500/30';
}

// Mobile card component for a single result
function ResultCard({ row, index }: { row: OptionResult; index: number }) {
  const [expanded, setExpanded] = useState(false);
  
  const premium = row.premium ?? row.lastPrice ?? 0;
  const dte = row.dte ?? row.calendar_days ?? 0;
  const oi = row.open_interest ?? row.openInterest ?? 0;
  const iv = row.implied_volatility ?? row.impliedVolatility ?? 0;
  
  return (
    <div 
      key={`${row.symbol}-${row.strike}-${row.expiry}-${index}`}
      className="bg-dark-800/50 rounded-xl border border-white/10 p-4 mb-3"
    >
      {/* Header row: Symbol, Price, Return badge */}
      <div className="flex items-center justify-between mb-3">
        <div className="flex items-center gap-3">
          <span className="text-lg font-bold text-white">{row.symbol}</span>
          <span className="text-gray-400">{formatCurrency(row.current_price)}</span>
        </div>
        <span className={`px-3 py-1 rounded-full text-sm font-semibold border ${getReturnBgClass(row.annualized_return)} ${getReturnClass(row.annualized_return)}`}>
          {formatPercent(row.annualized_return)}
        </span>
      </div>
      
      {/* Main metrics row */}
      <div className="grid grid-cols-2 gap-x-4 gap-y-2 text-sm mb-2">
        <div className="flex justify-between">
          <span className="text-gray-500">Strike:</span>
          <span className="text-white font-medium">{formatCurrency(row.strike)}</span>
        </div>
        <div className="flex justify-between">
          <span className="text-gray-500">Premium:</span>
          <span className="text-white font-medium">{formatCurrency(premium)}</span>
        </div>
        <div className="flex justify-between">
          <span className="text-gray-500">Prob:</span>
          <span className="text-white font-medium">{formatPercent(row.prob_assign)}</span>
        </div>
        <div className="flex justify-between">
          <span className="text-gray-500">DTE:</span>
          <span className="text-white font-medium">{dte}</span>
        </div>
      </div>
      
      {/* Expiry row */}
      <div className="flex justify-between text-sm border-t border-white/5 pt-2">
        <span className="text-gray-500">Expiry:</span>
        <span className="text-gray-300">{row.expiry}</span>
      </div>
      
      {/* Expandable details */}
      <button 
        onClick={() => setExpanded(!expanded)}
        className="w-full mt-2 pt-2 border-t border-white/5 flex items-center justify-center gap-1 text-xs text-gray-500 hover:text-gray-300 transition-colors"
      >
        {expanded ? (
          <>Less details <ChevronUp size={14} /></>
        ) : (
          <>More details <ChevronDown size={14} /></>
        )}
      </button>
      
      {expanded && (
        <div className="grid grid-cols-2 gap-x-4 gap-y-2 text-sm mt-3 pt-3 border-t border-white/5">
          <div className="flex justify-between">
            <span className="text-gray-500">Daily Decay:</span>
            <span className="text-white">{row.daily_decay_contract ? `$${row.daily_decay_contract.toFixed(4)}` : '-'}</span>
          </div>
          <div className="flex justify-between">
            <span className="text-gray-500">Volume:</span>
            <span className="text-white">{row.volume}</span>
          </div>
          <div className="flex justify-between">
            <span className="text-gray-500">Open Int:</span>
            <span className="text-white">{oi}</span>
          </div>
          <div className="flex justify-between">
            <span className="text-gray-500">IV:</span>
            <span className="text-white">{formatPercent(iv)}</span>
          </div>
        </div>
      )}
    </div>
  );
}

// Desktop table component
function DesktopTable({ data }: { data: OptionResult[] }) {
  return (
    <div className="overflow-x-auto rounded-xl shadow-lg shadow-black/20 border border-white/5">
      <table className="data-table">
        <thead>
          <tr>
            <th>Symbol</th>
            <th>Price</th>
            <th>Strike</th>
            <th>Premium</th>
            <th>Return</th>
            <th>Daily Decay</th>
            <th>Prob Assign</th>
            <th>Expiry</th>
            <th>DTE</th>
            <th>Vol</th>
            <th>OI</th>
            <th>IV</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => {
            const premium = row.premium ?? row.lastPrice ?? 0;
            const dte = row.dte ?? row.calendar_days ?? 0;
            const oi = row.open_interest ?? row.openInterest ?? 0;
            const iv = row.implied_volatility ?? row.impliedVolatility ?? 0;
            
            return (
              <tr key={`${row.symbol}-${row.strike}-${row.expiry}-${index}`}>
                <td className="font-medium">{row.symbol}</td>
                <td>{formatCurrency(row.current_price)}</td>
                <td>{formatCurrency(row.strike)}</td>
                <td>{formatCurrency(premium)}</td>
                <td>
                  <span className={getReturnClass(row.annualized_return)}>
                    {formatPercent(row.annualized_return)}
                  </span>
                </td>
                <td>{row.daily_decay_contract ? `$${row.daily_decay_contract.toFixed(4)}` : '-'}</td>
                <td>{formatPercent(row.prob_assign)}</td>
                <td>{row.expiry}</td>
                <td>{dte}</td>
                <td>{row.volume}</td>
                <td>{oi}</td>
                <td>{formatPercent(iv)}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export function ResultsTable({ data, title }: ResultsTableProps) {
  if (!data || data.length === 0) {
    return (
      <div className="text-center text-gray-400 py-8">
        No results for {title}
      </div>
    );
  }

  return (
    <>
      {/* Mobile: Card layout */}
      <div className="md:hidden">
        {data.map((row, index) => (
          <ResultCard key={`card-${row.symbol}-${row.strike}-${row.expiry}-${index}`} row={row} index={index} />
        ))}
      </div>
      
      {/* Desktop: Table layout */}
      <div className="hidden md:block">
        <DesktopTable data={data} />
      </div>
    </>
  );
}
