/**
 * Screening progress indicator component
 * - Portions generated by AI
 */

import { X, Loader2 } from 'lucide-react';
import { useAppStore } from '@/stores/useAppStore';

export function ScreeningProgress() {
  const { screeningProgress, requestStop, stopRequested } = useAppStore();

  if (!screeningProgress || !screeningProgress.isActive) {
    return null;
  }

  const { currentSymbol, completedCount, totalCount, startTime } = screeningProgress;
  const progressPercent = totalCount > 0 ? (completedCount / totalCount) * 100 : 0;

  // Calculate estimated time remaining
  let estimatedRemaining: string | null = null;
  if (startTime && completedCount > 0) {
    const elapsed = Date.now() - startTime;
    const avgTimePerSymbol = elapsed / completedCount;
    const remainingSymbols = totalCount - completedCount;
    const remainingMs = avgTimePerSymbol * remainingSymbols;
    const remainingSec = Math.ceil(remainingMs / 1000);
    
    if (remainingSec > 60) {
      const mins = Math.floor(remainingSec / 60);
      const secs = remainingSec % 60;
      estimatedRemaining = `~${mins}m ${secs}s remaining`;
    } else if (remainingSec > 0) {
      estimatedRemaining = `~${remainingSec}s remaining`;
    }
  }

  return (
    <div className="bg-dark-800/80 backdrop-blur-sm border border-white/10 rounded-xl p-4 mb-6 shadow-xl">
      {/* Progress bar with percentage */}
      <div className="flex items-center gap-3 mb-3">
        <div className="relative flex-1 h-2 bg-dark-700 rounded-full overflow-hidden">
          <div
            className="absolute inset-y-0 left-0 bg-gradient-to-r from-primary-500 to-secondary-500 rounded-full transition-all duration-300 ease-out"
            style={{ width: `${progressPercent}%` }}
          />
          {/* Animated shimmer effect */}
          <div
            className="absolute inset-y-0 left-0 bg-gradient-to-r from-transparent via-white/20 to-transparent animate-shimmer"
            style={{ width: `${progressPercent}%` }}
          />
        </div>
        <span className="text-sm font-medium text-primary-400 tabular-nums w-12 text-right">
          {Math.round(progressPercent)}%
        </span>
      </div>

      {/* Progress details */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-3">
          <Loader2 size={18} className="text-primary-400 animate-spin" />
          <div>
            <div className="flex items-center gap-2">
              <span className="text-sm font-medium">
                Processing{' '}
                <span className="text-primary-400">
                  {currentSymbol || '...'}
                </span>
              </span>
              <span className="text-sm text-gray-400">
                ({completedCount} of {totalCount})
              </span>
            </div>
            {estimatedRemaining && (
              <p className="text-xs text-gray-500 mt-0.5">{estimatedRemaining}</p>
            )}
          </div>
        </div>

        {/* Cancel button */}
        <button
          onClick={requestStop}
          disabled={stopRequested}
          className="flex items-center gap-1.5 px-3 py-1.5 text-sm text-gray-400 hover:text-red-400 
                     bg-dark-700 hover:bg-red-500/10 rounded-lg transition-colors
                     disabled:opacity-50 disabled:cursor-not-allowed"
        >
          <X size={14} />
          {stopRequested ? 'Stopping...' : 'Cancel'}
        </button>
      </div>
    </div>
  );
}
