/**
 * Sidebar component - displays current selection info
 * Simplified: No manual controls, just shows selected Filter and Trade Idea
 * - Portions generated by AI
 */

import { ChevronLeft, ChevronRight, X, Filter, Lightbulb } from 'lucide-react';
import { useAppStore } from '@/stores/useAppStore';

export function Sidebar() {
  const { 
    sidebarOpen, 
    toggleSidebar,
    selectedFilter,
    selectedTradeIdea,
  } = useAppStore();

  // Hidden state - show toggle button
  if (!sidebarOpen) {
    return (
      <button
        onClick={toggleSidebar}
        className="fixed left-0 top-1/2 -translate-y-1/2 z-50 bg-dark-800 p-3 sm:p-2.5 rounded-r-lg border border-l-0 border-white/10 hover:bg-dark-700 transition-colors text-gray-400 hover:text-white shadow-lg"
        title="Open sidebar"
      >
        <ChevronRight size={22} className="sm:w-5 sm:h-5" />
      </button>
    );
  }

  return (
    <>
      {/* Mobile backdrop overlay */}
      <div 
        className="fixed inset-0 bg-black/60 z-40 sm:hidden"
        onClick={toggleSidebar}
      />
      
      {/* Sidebar - full screen on mobile, side panel on desktop */}
      <aside className="fixed sm:relative inset-0 sm:inset-auto w-full sm:w-72 bg-dark-900 border-r border-white/5 p-5 sm:p-4 flex flex-col gap-5 sm:gap-4 overflow-y-auto z-50">
        {/* Header with title (mobile) and close button */}
        <div className="flex items-center justify-between sm:justify-end mb-2 sm:mb-0">
          <h2 className="text-lg font-semibold text-white sm:hidden">Current Selection</h2>
          <button
            onClick={toggleSidebar}
            className="p-2.5 sm:p-1.5 hover:bg-dark-800 rounded-lg transition-colors text-gray-400 hover:text-white"
            title="Close sidebar"
          >
            <X size={22} className="sm:hidden" />
            <ChevronLeft size={18} className="hidden sm:block" />
          </button>
        </div>

        {/* Selected Trade Idea */}
        <div className="bg-dark-800/50 rounded-xl p-4 border border-white/5">
          <div className="flex items-center gap-2 mb-3">
            <Lightbulb size={18} className="text-primary-400" />
            <p className="text-sm font-semibold text-primary-400 uppercase tracking-wider">
              Trade Idea
            </p>
          </div>
          
          {selectedTradeIdea ? (
            <div className="overflow-hidden">
              <div className="flex items-start gap-2 mb-1">
                <p className="text-lg font-medium text-white truncate flex-1" title={selectedTradeIdea.name}>
                  {selectedTradeIdea.name}
                </p>
                {selectedTradeIdea.is_system && (
                  <span className="text-xs bg-blue-500/20 text-blue-400 px-2 py-0.5 rounded whitespace-nowrap flex-shrink-0">
                    System
                  </span>
                )}
              </div>
              {selectedTradeIdea.description && (
                <p className="text-sm text-gray-400 mb-3 line-clamp-2">{selectedTradeIdea.description}</p>
              )}
              <div className="flex flex-wrap gap-1.5">
                {selectedTradeIdea.symbols.slice(0, 8).map((symbol) => (
                  <span
                    key={symbol}
                    className="px-2 py-0.5 bg-dark-700 text-gray-300 rounded text-xs font-medium"
                  >
                    {symbol}
                  </span>
                ))}
                {selectedTradeIdea.symbols.length > 8 && (
                  <span className="px-2 py-0.5 text-gray-500 text-xs">
                    +{selectedTradeIdea.symbols.length - 8} more
                  </span>
                )}
              </div>
            </div>
          ) : (
            <p className="text-gray-500 text-sm">No trade idea selected</p>
          )}
        </div>

        {/* Selected Filter */}
        <div className="bg-dark-800/50 rounded-xl p-4 border border-white/5">
          <div className="flex items-center gap-2 mb-3">
            <Filter size={18} className="text-primary-400" />
            <p className="text-sm font-semibold text-primary-400 uppercase tracking-wider">
              Filter
            </p>
          </div>
          
          {selectedFilter ? (
            <div className="overflow-hidden">
              <div className="flex items-start gap-2 mb-3">
                <p className="text-lg font-medium text-white truncate flex-1" title={selectedFilter.name}>
                  {selectedFilter.name}
                </p>
                {selectedFilter.is_system && (
                  <span className="text-xs bg-blue-500/20 text-blue-400 px-2 py-0.5 rounded whitespace-nowrap flex-shrink-0">
                    System
                  </span>
                )}
              </div>
              <div className="grid grid-cols-2 gap-3 text-sm">
                <div>
                  <p className="text-gray-500 text-xs">DTE Range</p>
                  <p className="text-white">{selectedFilter.min_dte} - {selectedFilter.max_dte} days</p>
                </div>
                <div>
                  <p className="text-gray-500 text-xs">Min Volume</p>
                  <p className="text-white">{selectedFilter.min_volume}</p>
                </div>
                <div>
                  <p className="text-gray-500 text-xs">Min Open Interest</p>
                  <p className="text-white">{selectedFilter.min_open_interest}</p>
                </div>
                <div>
                  <p className="text-gray-500 text-xs">Min Return</p>
                  <p className="text-white">{selectedFilter.min_annualized_return}%</p>
                </div>
                <div className="col-span-2">
                  <p className="text-gray-500 text-xs">Max Assignment Probability</p>
                  <p className="text-white">{selectedFilter.max_assignment_probability}%</p>
                </div>
              </div>
            </div>
          ) : (
            <p className="text-gray-500 text-sm">No filter selected</p>
          )}
        </div>

      </aside>
    </>
  );
}
