/**
 * Dashboard page - main screener interface
 * - Portions generated by AI
 */

import { useCallback } from 'react';
import { Play, Loader2, ChevronDown } from 'lucide-react';
import { useAppStore } from '@/stores/useAppStore';
import apiClient from '@/api/client';
import { Sidebar } from '@/components/Sidebar';
import { ResultsTable } from '@/components/ResultsTable';
import { NewsSection } from '@/components/NewsSection';
import { ScreeningProgress } from '@/components/ScreeningProgress';
import type { OptionResult } from '@/types';

export function Dashboard() {
  const {
    settings,
    results,
    setResults,
    appendResult,
    selectedSymbol,
    setSelectedSymbol,
    selectedView,
    setSelectedView,
    isScreening,
    usedYahooFallback,
    setUsedYahooFallback,
    sidebarOpen,
    screeningProgress,
    startScreening,
    updateScreeningSymbol,
    incrementProgress,
    stopScreening,
    stopRequested,
  } = useAppStore();

  // Build summary from current results
  const buildSummary = useCallback((currentResults: Record<string, OptionResult[]>) => {
    const symbols = Object.keys(currentResults);
    if (symbols.length > 1) {
      const summaryResults: OptionResult[] = [];
      for (const symbol of symbols) {
        if (currentResults[symbol] && currentResults[symbol].length > 0) {
          summaryResults.push(currentResults[symbol][0]);
        }
      }
      setResults({ ...currentResults, Summary: summaryResults });
      setSelectedView('Summary');
    } else if (symbols.length === 1) {
      setSelectedView(symbols[0]);
    }
  }, [setResults, setSelectedView]);

  // Sequential screening with progress
  const handleScreenAll = useCallback(async () => {
    const symbols = settings.symbols;
    if (symbols.length === 0) return;

    startScreening(symbols.length);
    let yahooUsed = false;
    const allResults: Record<string, OptionResult[]> = {};

    for (const symbol of symbols) {
      // Check if stop was requested
      if (useAppStore.getState().stopRequested) {
        break;
      }

      updateScreeningSymbol(symbol);

      try {
        const response = await apiClient.screenOptions({
          symbols: [symbol],
          max_dte: settings.max_dte,
          min_dte: settings.min_dte,
          min_volume: settings.min_volume,
          min_open_interest: settings.min_open_interest,
          min_annualized_return: settings.min_annualized_return,
          max_assignment_probability: settings.max_assignment_probability,
        });

        if (response.used_yahoo_fallback) {
          yahooUsed = true;
        }

        const symbolResults = response.results[symbol] || [];
        if (symbolResults.length > 0) {
          allResults[symbol] = symbolResults;
          appendResult(symbol, symbolResults);
        }
      } catch (error) {
        console.error(`Error screening ${symbol}:`, error);
        // Continue with next symbol
      }

      incrementProgress();
    }

    setUsedYahooFallback(yahooUsed);
    stopScreening();
    buildSummary(allResults);
  }, [
    settings,
    startScreening,
    updateScreeningSymbol,
    appendResult,
    incrementProgress,
    stopScreening,
    setUsedYahooFallback,
    buildSummary,
  ]);

  // Single symbol screening (no progress needed)
  const handleScreenSingle = useCallback(async () => {
    if (!selectedSymbol) return;

    startScreening(1);
    updateScreeningSymbol(selectedSymbol);

    try {
      const response = await apiClient.screenOptions({
        symbols: [selectedSymbol],
        max_dte: settings.max_dte,
        min_dte: settings.min_dte,
        min_volume: settings.min_volume,
        min_open_interest: settings.min_open_interest,
        min_annualized_return: settings.min_annualized_return,
        max_assignment_probability: settings.max_assignment_probability,
      });

      setUsedYahooFallback(response.used_yahoo_fallback);
      setResults(response.results);
      
      if (Object.keys(response.results).length > 0) {
        setSelectedView(selectedSymbol);
      }
    } catch (error) {
      console.error(`Error screening ${selectedSymbol}:`, error);
    }

    incrementProgress();
    stopScreening();
  }, [
    selectedSymbol,
    settings,
    startScreening,
    updateScreeningSymbol,
    setResults,
    setSelectedView,
    incrementProgress,
    stopScreening,
    setUsedYahooFallback,
  ]);

  // Build view options
  const viewOptions = Object.keys(results).filter(k => k !== 'Summary');
  if (results.Summary) {
    viewOptions.unshift('Summary');
  }

  const currentResults = results[selectedView] || [];

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      {sidebarOpen && <Sidebar />}

      {/* Main content */}
      <main className="flex-1 overflow-y-auto p-6">
        {/* Header */}
        <div className="mb-6">
          <h1 className="text-3xl font-bold gradient-text mb-2">
            Put Options Screener
          </h1>
          <p className="text-gray-400">
            Discover profitable put option opportunities with real-time market data
          </p>
        </div>

        {/* Action row */}
        <div className="flex flex-wrap items-center gap-4 mb-6">
          {/* Symbol selector */}
          <div className="relative">
            <select
              value={selectedSymbol}
              onChange={(e) => setSelectedSymbol(e.target.value)}
              disabled={isScreening}
              className="appearance-none bg-dark-800 border border-white/10 rounded-lg px-4 py-2.5 pr-10 text-sm focus:border-primary-500 min-w-[140px] disabled:opacity-50"
            >
              {settings.symbols.map((symbol) => (
                <option key={symbol} value={symbol}>
                  {symbol}
                </option>
              ))}
            </select>
            <ChevronDown
              size={16}
              className="absolute right-3 top-1/2 -translate-y-1/2 pointer-events-none text-gray-400"
            />
          </div>

          {/* Screen All button */}
          <button
            onClick={handleScreenAll}
            disabled={isScreening || settings.symbols.length === 0}
            className="btn-primary flex items-center gap-2"
          >
            {isScreening ? (
              <Loader2 size={16} className="animate-spin" />
            ) : (
              <Play size={16} />
            )}
            Screen All
          </button>

          {/* Screen Single button */}
          <button
            onClick={handleScreenSingle}
            disabled={isScreening || !selectedSymbol}
            className="btn-secondary flex items-center gap-2"
          >
            Screen Stock
          </button>
        </div>

        {/* Screening progress indicator */}
        {screeningProgress && screeningProgress.isActive && (
          <ScreeningProgress />
        )}

        {/* Results section */}
        {Object.keys(results).length > 0 ? (
          <>
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-xl font-semibold">
                Results
                {isScreening && (
                  <span className="text-sm font-normal text-gray-400 ml-2">
                    (updating...)
                  </span>
                )}
              </h2>
              
              {/* View selector */}
              {viewOptions.length > 1 && (
                <div className="relative">
                  <select
                    value={selectedView}
                    onChange={(e) => setSelectedView(e.target.value)}
                    className="appearance-none bg-dark-800 border border-white/10 rounded-lg px-4 py-2 pr-10 text-sm focus:border-primary-500 min-w-[120px]"
                  >
                    {viewOptions.map((option) => (
                      <option key={option} value={option}>
                        {option}
                      </option>
                    ))}
                  </select>
                  <ChevronDown
                    size={16}
                    className="absolute right-3 top-1/2 -translate-y-1/2 pointer-events-none text-gray-400"
                  />
                </div>
              )}
            </div>

            <ResultsTable data={currentResults} title={selectedView} />

            {/* Yahoo fallback notice */}
            {usedYahooFallback && !isScreening && (
              <p className="text-gray-500 text-sm mt-4">
                Note: When Massive.com data was unavailable, prices or options data were retrieved from Yahoo Finance.
              </p>
            )}

            {/* News section - only show when not screening */}
            {selectedView !== 'Summary' && !isScreening && (
              <NewsSection symbol={selectedView} />
            )}
          </>
        ) : !isScreening ? (
          <div className="card text-center py-12">
            <p className="text-gray-400">
              Select a stock and click <strong>Screen Stock</strong> to find put options,
              or click <strong>Screen All</strong> to analyze your watchlist.
            </p>
          </div>
        ) : null}

      </main>
    </div>
  );
}
